mapper.delete:
delete from neuedu_cart

  and id in

    #{cart.id}

delete from neuedu_cart

  user_id=#{userId}

    and product_id in

      #{productId}

mapper.select:

select id, user_id, product_id, quantity, checked, create_time, update_time
from neuedu_cart
where id = #{id,jdbcType=INTEGER}

select id, user_id, product_id, quantity, checked, create_time, update_time
from neuedu_cart
where user_id=#{userId}
and product_id=#{productId}

select id, user_id, product_id, quantity, checked, create_time, update_time
 from neuedu_cart
 where user_id=#{userId}

select ifnull(sum(quantity),0)
 from neuedu_cart
 where user_id=#{userId}

mapper.update:
update neuedu_cart
set checked=#{check}

  user_id=#{userId}

    and product_id=#{productId}
